mapper.select:
select b.*, e.name from board b join employees e on b.e_idx = e.e_idx where b.status = 'N' and b.type = #{type}
and b.title like CONCAT('%', #{searchWord}, '%')
and e.name like CONCAT('%', #{searchWord}, '%')
and b.content like CONCAT('%', #{searchWord}, '%')
order by b.post_date desc limit #{startNum}, #{pageSize}
